import React from "react";
import PropTypes from "prop-types";

const CartItem = ({ image, name, price, onClick }) => {
   return (
       <div className="cart-item">
        <div>
            <ul>
                <li className="cart-item-image"{...image}></li>
                <li className="cart-item-name"{...name}></li>
                <li className="cart-item-price"{...price}></li>
            </ul>
            <button className="btn btn-danger btn" onClick={onClick}>Delete</button>
        </div>
        </div>
   );
   
}
CartItem.propTypes = {
    image: PropTypes.string.isRequired,
    name: PropTypes.number.isRequired,
    price: PropTypes.string.isRequired,
    onClick: PropTypes.func.isRequired
}

export default CartItem;
